mapper.insert:
INSERT INTO user_challenge (
    user_id, fund_id
)
VALUES (
           #{userId}, #{fundId}
       )

mapper.update:
UPDATE user_challenge
SET current_count = current_count + 1
WHERE user_challenge_id = #{userChallengeId}

UPDATE user_challenge
SET fail_count = fail_count + 1
WHERE user_challenge_id = #{userChallengeId}

UPDATE user_challenge
SET status = #{status}
WHERE user_challenge_id = #{userChallengeId}

mapper.select:
SELECT COUNT(*) > 0
FROM user_challenge
WHERE user_challenge_id = #{userChallengeId}
AND user_id = #{userId}

SELECT COUNT(*) > 0
FROM user_challenge
WHERE user_id = #{userId} AND fund_id = #{fundId}

mapper.update:
UPDATE user_challenge
SET current_count = current_count + 1
WHERE user_challenge_id = #{userChallengeId}

UPDATE user_challenge
SET fail_count = fail_count + 1
WHERE user_challenge_id = #{userChallengeId}
